import React from 'react';

import VideoThumbnail from 'react-video-thumbnail';
import vdetails from './components/vdetails.json'

const detail = vdetails;

export default class Vplayer extends React.Component {
    constructor(props) {
        super(props);
        this.state = ({
            vurl: ''
        })
    }
    onClick() {
        this.setState({
            vurl: detail.url
        })
    }
    render() {
        return(
            <div>
                <div>
                    <iframe width="560" height="315" src={this.state.vurl} title="YouTube video player" frameBorder="0" allowFullScreen></iframe>
                </div>
                <div>
                    <VideoThumbnail
                        videoUrl="https://dl.dropboxusercontent.com/s/7b21gtvsvicavoh/statue-of-admiral-yi-no-audio.mp4?dl=1"
                        thumbnailHandler={(thumbnail) => console.log(thumbnail)}
                        width={120}
                        height={80}
                    />
                </div>
            </div>
        )
    }
}
